def main() -> None:
    """Perform the rerun."""
    setup_logging()

    individual: Individual = pickle.loads(PICKLED_GENOTYPE)
    robot = individual.genotype.develop()

    logging.info(f"Fitness from pickle: {individual.fitness}")

